import React, { useEffect } from "react";
import useStyles from "./styles";
import { TextField, Button, Typography, Paper, Box } from "@mui/material";
import { useState } from "react";
import { createPost, updatePost } from "../../reducers/postReducer";
import FileBase from "react-file-base64";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { setId } from "../../reducers/idReducer";

const Form = ({ setMessage, setTypeOfMessage }) => {
  const dispatch = useDispatch();
  const [postInfo, setPostInfo] = useState({
    creator: "",
    title: "",
    message: "",
    tags: "",
    selectedFile: "",
  });
  const classes = useStyles();
  const currentId = useSelector((state) => state.currentId);
  const post = useSelector((state) =>
    currentId ? state.posts.find((post) => post._id === currentId) : null
  );

  useEffect(() => {
    if (post) {
      setPostInfo(post);
    }
  }, [post]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const userIsSignedIn = localStorage.getItem("profile");

    if (userIsSignedIn) {
      if (currentId) {
        dispatch(updatePost(currentId, postInfo));
      } else {
        dispatch(createPost(setMessage, setTypeOfMessage, postInfo));
      }
      clear();
    } else {
      setTypeOfMessage("warning");
      setMessage(
        "You can only create, edit and delete a post if you are signed in and if you have created it."
      );
      setTimeout(() => {
        setMessage(null);
        setTypeOfMessage(null);
      }, 6000);
    }
  };

  const clear = () => {
    dispatch(setId(null));
    setPostInfo({
      creator: "",
      title: "",
      message: "",
      tags: "",
      selectedFile: "",
    });
  };

  return (
    <Paper className={classes.paper}>
      <form
        autoComplete="off"
        noValidate
        className={`{classes.root} {classes.form}`}
        onSubmit={handleSubmit}
      >
        <Typography variant="h6" textAlign="center">
          {currentId ? "Editing" : "Creating"} a Memory
        </Typography>
        <TextField
          margin="dense"
          name="creator"
          variant="outlined"
          label="Creator"
          fullWidth
          value={postInfo.creator}
          onChange={(e) =>
            setPostInfo({ ...postInfo, creator: e.target.value })
          }
        />
        <TextField
          margin="dense"
          name="title"
          variant="outlined"
          label="Title"
          fullWidth
          value={postInfo.title}
          onChange={(e) => setPostInfo({ ...postInfo, title: e.target.value })}
        />
        <TextField
          margin="dense"
          name="message"
          variant="outlined"
          label="Message"
          fullWidth
          value={postInfo.message}
          onChange={(e) =>
            setPostInfo({ ...postInfo, message: e.target.value })
          }
        />
        <TextField
          margin="dense"
          name="tags"
          variant="outlined"
          label="Tags"
          fullWidth
          value={postInfo.tags}
          onChange={(e) =>
            setPostInfo({
              ...postInfo,
              tags: e.target.value.split(","),
            })
          }
        />
        <div className={classes.fileInput}>
          <FileBase
            type="file"
            multiple={false}
            onDone={({ base64 }) =>
              setPostInfo({ ...postInfo, selectedFile: base64 })
            }
          />
        </div>
        <Box marginBottom={1}>
          <Button
            className={classes.buttonSubmit}
            variant="contained"
            color="primary"
            size="large"
            type="submit"
            fullWidth
          >
            Submit
          </Button>
        </Box>
        <Box marginBottom={1}>
          <Button
            variant="contained"
            color="secondary"
            size="small"
            onClick={clear}
            fullWidth
          >
            Clear
          </Button>
        </Box>
      </form>
    </Paper>
  );
};

export default Form;
