import React from "react";
import Post from "./Post/Post";
import useStyles from "./styles";
import { Grid, CircularProgress } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { removePost, likeAPost } from "../../reducers/postReducer";

const Posts = () => {
  const posts = useSelector((state) => state.posts);
  const classes = useStyles();
  const dispatch = useDispatch();
  const updatePost = (id) => {
    console.log(`I will update the post with the following id: ${id}`);
  };

  const deletePost = (id) => {
    dispatch(removePost(id));
  };

  const addALike = (id) => {
    dispatch(likeAPost(id));
  };

  return (
    <Grid
      className={classes.mainContainer}
      container
      alignItems="stretch"
      spacing={3}
    >
      {posts.map((p) => (
        <Grid item key={p._id} ks={12} sm={6}>
          <Post
            post={p}
            updatePost={() => updatePost(p._id)}
            removePost={() => deletePost(p._id)}
          />
        </Grid>
      ))}
    </Grid>
  );
};

export default Posts;
