import { createSlice } from "@reduxjs/toolkit";
import posts from "../services/posts";
import postService from "../services/posts";

const postSlice = createSlice({
  name: posts,
  initialState: [],
  reducers: {
    setPosts(state, action) {
      return action.payload;
    },
    appendPost(state, action) {
      state.push(action.payload);
    },
    detachPost(state, action) {
      const id = action.payload;
      return state.filter((post) => post.id !== id);
    },
    updateLike(state, action) {
      const id = action.payload;
      const postToLike = state.find((post) => post.id === id);
      const likedPost = { ...postToLike, likes: postToLike.likes + 1 };
      return state.map((post) => (post.id === id ? likedPost : post));
    },
  },
});

export const { setPosts, appendPost, detachPost } = postSlice.actions;

export const initialisePosts = () => {
  return async (dispatch) => {
    const posts = await postService.getAll();
    dispatch(setPosts(posts));
    console.log(posts);
  };
};

export const createPost = (obj) => {
  return async (dispatch) => {
    try {
      const newPost = await postService.create(obj);
      dispatch(appendPost(newPost));
    } catch (error) {
      console.log("Could not save the new post");
    }
  };
};

export const removePost = (id) => {
  return async (dispatch) => {
    try {
      await postService.removeBlog(id);
      dispatch(detachPost(id));
    } catch (error) {
      console.log("Could not delete the post");
    }
  };
};

export default postSlice.reducer;
