import axios from "axios";
const baseUrl = "/api/posts";

const setToken = async () => {
  let user = await JSON.parse(localStorage.getItem("profile"));
  let token = `bearer ${user.token}`; // take it from the local storage
  return token;
};

const getAll = async () => {
  const response = await axios.get(baseUrl);
  return response.data;
};

const create = async (newObj) => {
  const config = {
    headers: { Authorization: setToken() },
  };

  const response = await axios.post(baseUrl, newObj, config);
  return response.data;
};

const update = async (id, newObject) => {
  const response = await axios.put(`${baseUrl}/${id}`, newObject);
  return response.data;
};

const removeThePost = async (id) => {
  const config = {
    headers: { Authorization: setToken() },
  };
  const response = await axios.delete(`${baseUrl}/${id}`, config);
  return response.data;
};

export default { getAll, create, removeThePost, update };
