import { initializeApp } from "firebase/app";
import {
  getAuth,
  GoogleAuthProvider,
  signInWithPopup,
  // signOut,
} from "firebase/auth";
import { useDispatch } from "react-redux";
import { setAuth } from "../../reducers/auth";

const firebaseConfig = {
  apiKey: "AIzaSyA8ecbKXZVUsVK46W0x7i5g7Q6x36fmKnw",
  authDomain: "ricordi-app-c6539.firebaseapp.com",
  projectId: "ricordi-app-c6539",
  storageBucket: "ricordi-app-c6539.appspot.com",
  messagingSenderId: "196097898000",
  appId: "1:196097898000:web:511ccc0198412ed9bc3c95",
  measurementId: "G-MTY67KLYSP",
};

const app = initializeApp(firebaseConfig);

export const auth = getAuth(app); // this allows us to get who is currently authenticated in our app

const provider = new GoogleAuthProvider();
provider.setCustomParameters({
  prompt: "select_account",
});

export const signInWithGoogle = () => {
  const dispatch = useDispatch;

  signInWithPopup(auth, provider)
    .then((result) => {
      console.log(result);
      const token = result.user.accesToken;
      dispatch(setAuth());
    })
    .catch((error) => {
      console.log(error);
      console.log("Google sign in was unsuccesfull. Try again.");
    });
};

